import { useEffect, useState } from "react";
import { FaCamera } from "react-icons/fa";
import { AiOutlineEye, AiOutlineEyeInvisible } from "react-icons/ai";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "sonner";
import useAxiosSecure from "../../Hooks/useAxiosSecure";

const RegistrationTrade = () => {
  const [imagePreview, setImagePreview] = useState("");
  const [showCompanyInput, setShowCompanyInput] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [certificationPreview, setCertificationPreview] = useState("");
  const [errors, setErrors] = useState({});
  const [trades, setTrades] = useState([]);
  const [loading, setLoading] = useState(false);

  const axiosSecure = useAxiosSecure();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
    trade: "",
    postcode: "",
    experience: "",
    companyName: "",
    registrationNumber: "",
  });

  useEffect(() => {
    const getTrades = async () => {
      try {
        const res = await axiosSecure.get("/trades");
        setTrades(res.data?.data?.trades);
      } catch (error) {
        console.log(error);
        toast.error(error.data?.message || "something went wrong");
      }
    };

    getTrades();
  }, []);


  const [images, setImages] = useState({
    profileImage: null,
    insuranceImage: null,
    licenseImage: null,
  });

  // Input Change Handler
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImages({ ...images, profileImage: file });
      const reader = new FileReader();
      reader.onload = () => setImagePreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleCertificationUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImages({ ...images, licenseImage: file });
      const reader = new FileReader();
      reader.onload = () => setCertificationPreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const toggleCompanyInput = () => setShowCompanyInput(!showCompanyInput);


  // RegistrationTrade.jsx
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (loading) return;

    if (formData.password !== formData.confirmPassword) {
      toast.error("Passwords don't match!");
      return;
    }

    if (!images.profileImage) {
      toast.error("Upload a profile image!");
      return;
    }

    try {
      setLoading(true);

      // Validate company information if needed
      if (showCompanyInput) {
        if (!formData.companyName) {
          throw new Error("Company name is required!");
        }
        if (!formData.registrationNumber) {
          throw new Error("Company registration number is required!");
        }
        if (!images.insuranceImage) {
          throw new Error("Company insurance is required!");
        }
      }

      // Create FormData object
      const formDataToSend = new FormData();

      // Add form fields (excluding confirmPassword)
      Object.entries(formData).forEach(([key, value]) => {
        if (key !== 'confirmPassword' && value) {
          formDataToSend.append(key, value);
        }
      });

      // Add images
      if (images.profileImage) {
        formDataToSend.append('profileImage', images.profileImage);
      }
      if (images.insuranceImage) {
        formDataToSend.append('insuranceImage', images.insuranceImage);
      }
      if (images.licenseImage) {
        formDataToSend.append('licenseImage', images.licenseImage);
      }

      const response = await axiosSecure.post(
        "/auth/tradesperson/register",
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.success) {
        toast.success(response.data.message);

        // Reset form
        setFormData({
          firstName: "",
          lastName: "",
          email: "",
          phone: "",
          password: "",
          confirmPassword: "",
          trade: "",
          postcode: "",
          experience: "",
          companyName: "",
          registrationNumber: ""
        });

        setImages({
          profileImage: null,
          insuranceImage: null,
          licenseImage: null
        });

        setImagePreview("");
        setCertificationPreview("");
        setShowCompanyInput(false);

        // Navigate after a short delay
        setTimeout(() => {
          navigate('/account/login');
        }, 1500); // Give time for the success message to be seen
      }

    } catch (error) {
      const errorMessage = error.response?.data?.message || error.message || "Registration failed";
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form
        onSubmit={(e) => handleSubmit(e)}
        className="w-full lg:my-10 max-w-4xl bg-white shadow-lg rounded-lg p-8 space-y-8"
      >
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-800">
            Create a Trade Account
          </h1>
          <p className="text-gray-600">Join and showcase your trade skills.</p>
        </div>

        {/* Image Upload */}
        <div className="flex justify-center items-center relative">
          <div className="relative group w-36 h-36">
            <img
              src={
                imagePreview ||
                "data:image/jpeg;base64,"
              }
              alt="Profile"
              className="w-36 h-36 rounded-full object-cover border-4 border-gray-300"
            />
            <label
              htmlFor="imageInput"
              className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-0 group-hover:bg-opacity-50 transition-opacity rounded-full cursor-pointer"
            >
              <FaCamera className="text-white text-2xl opacity-0 group-hover:opacity-100 transition-opacity" />
            </label>
            <input
              type="file"
              id="imageInput"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
            />
          </div>
        </div>
        {errors.profileImage && (
          <p className="text-red-500 text-center text-sm">
            {errors.profileImage}
          </p>
        )}

        {/* Personal Information */}
        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700">
              First Name <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="firstName"
              required
              type="text"
              placeholder="First Name"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Last Name <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="lastName"
              required
              type="text"
              placeholder="Last Name"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Email <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="email"
              required
              type="email"
              placeholder="Email Address"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Phone Number <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="phone"
              required
              type="text"
              placeholder="Phone Number"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>

        {/* Password Section */}
        <div className="grid md:grid-cols-2 gap-6">
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700">
              Password <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="password"
              required
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <div
              onClick={() => setShowPassword(!showPassword)}
              className="absolute inset-y-0 right-3 top-5 flex items-center cursor-pointer"
            >
              {showPassword ? <AiOutlineEyeInvisible /> : <AiOutlineEye />}
            </div>
          </div>
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700">
              Confirm Password <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="confirmPassword"
              required
              type={showConfirmPassword ? "text" : "password"}
              placeholder="Confirm Password"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <div
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              className="absolute inset-y-0 right-3 top-5 flex items-center cursor-pointer"
            >
              {showConfirmPassword ? (
                <AiOutlineEyeInvisible />
              ) : (
                <AiOutlineEye />
              )}
            </div>
          </div>
        </div>

        {/* Trade Information */}
        <div className="grid md:grid-cols-3 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Trade/Profession <span className="text-red-500">*</span>
            </label>
            <select
              required
              onChange={(e) => handleChange(e)}
              name="trade"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select Trade</option>
              {
                trades.map((trade) => <option key={trade._id} value={trade._id}>{trade.name}</option>)
              }
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Years of Experience <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="experience"
              required
              type="number"
              placeholder="Years of Experience"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Post Code <span className="text-red-500">*</span>
            </label>
            <input
              onChange={(e) => handleChange(e)}
              name="postcode"
              required
              type="number"
              placeholder="Post code"
              className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Certifications/Licenses (Upload){" "}
            <span className="text-red-500">*</span>
          </label>
          <div className="flex items-center space-x-4">
            <input
              type="file"
              required
              accept="image/*"
              onChange={handleCertificationUpload}
              className="w-full px-4 py-2 border rounded"
            />
            {certificationPreview && (
              <img
                src={certificationPreview}
                alt="Certification Preview"
                className="w-16 h-16 object-cover rounded"
              />
            )}
          </div>
        </div>

        {/* Company Information */}
        <div>
          <label className="flex items-center space-x-2">
            <input
              type="checkbox"
              onChange={toggleCompanyInput}
              className="checkbox checkbox-sm checkbox-success [--chkfg:white] rounded border-gray-300 focus:ring-green-500"
            />
            <span className="text-sm font-medium text-gray-700">
              Register as a company
            </span>
          </label>
          {showCompanyInput && (
            <div>
              <div className="grid md:grid-cols-2 gap-6 mt-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Company Name <span className="text-red-500">*</span>
                  </label>
                  <input
                    onChange={(e) => handleChange(e)}
                    name="companyName"
                    type="text"
                    placeholder="Company Name"
                    className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-green-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">
                    Proof of Insurance (Upload)
                  </label>
                  <input
                    onChange={(e) => {
                      setImages({
                        ...images,
                        insuranceImage: e.target.files[0],
                      });
                    }}
                    type="file"
                    accept=".jpg,.jpeg,.png"
                    className="w-full px-4 py-2 border rounded"
                  />
                </div>
              </div>
              <div className="mt-2">
                <label className="block text-sm font-medium text-gray-700">
                  Registration Number <span className="text-red-500"></span>
                </label>
                <input
                  onChange={(e) => handleChange(e)}
                  name="registrationNumber"
                  type="text"
                  placeholder="Registration Number"
                  className="w-full px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-green-500"
                />
              </div>
            </div>
          )}
        </div>

        <div className="flex w-full justify-end items-start gap-2">
          <input
            type="checkbox"
            required
            className="checkbox checkbox-sm checkbox-success [--chkfg:white] text-white "
          />
          <p className="text-sm font-medium text-gray-700">
            I agree to the <span className="text-blue-600">Terms of Use</span>{" "}
            and <span className="text-blue-600">Privacy Policy.</span>
          </p>
        </div>

        <div className="flex justify-center"> 
          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 text-white bg-green-500 hover:bg-green-600 rounded-lg shadow-md transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? (
              <div className="flex items-center justify-center">
                <svg
                  className="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Registering...
              </div>
            ) : (
              "Register"
            )}
          </button>
        </div>

        <div>
          <p className="text-center text-sm text-gray-700">
            <span className="">Already have account?</span>{" "}
            <Link
              to={"/account/login"}
              className="text-green-600 hover:underline underline-offset-4 underline"
            >
              login now
            </Link>
          </p>
        </div>
      </form>
    </div>
  );
};

export default RegistrationTrade;
